export class FooterComponentComponent {
    constructor(cfr, vcref) {
        this.cfr = cfr;
        this.vcref = vcref;
    }
    async ngOnInit() {
        const { FooterComponentRemote } = await loadRemoteModule({
            remoteEntry: 'http://localhost:4200/remoteEntry.js',
            remoteName: 'footer',
            exposedModule: './FooterComponent',
        });
        const componentRef = this.vcref.createComponent(this.cfr.resolveComponentFactory(FooterComponentRemote));
        componentRef.instance.year = 2022;
    }
}
